package chapter02;

public class Ex14_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// final 키워드 : 변수를 상수화할때 사용, 자주 사용하지 않음
		// 변수를 상수화한다 -> 변수에 한번 저장한 값을 바꾸지 않고 사용하겠다라는 것
		final int num = 10;
		System.out.println("num = " + num);
		
//		num = 20;
		System.out.println("num = " + num);
	}

}
