package chapter03;

public class Ex4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 비트연산자에는 이러한 기호들이 있다
		// 2진수로 계산해서 true false
		// 정보처리 기능사 필기 불대수 파트에 있음
		System.out.println(17 & 3);
		System.out.println(17 | 3);
		System.out.println(17 ^ 3);
		System.out.println(17 >> 3);
		System.out.println(17 << 3);
		
		
	}

}
