package chapter05;

public class Ex14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println("switch프로그램 시작");
		
		switch(1) {
			case 1:
				System.out.println("switch로 들어온 값 1입니다.");
				break;
			case 2:
				System.out.println("switch로 들어온 값은");
				System.out.println("2입니다.");
			case 3:
				System.out.println("switch로 들어온 값은 3입니다.");
		}
		
		System.out.println("switch 프로그램 종료");
		
		
		
	}

}